'''
This code creats the gui screen using VGA that the user will see. Exit by typing ^C
'''
import sys
import KEY
import video
import signal
import math
import time

boarder_color = 0x0000
dark_blue = 0x021A
background_color = video.CYAN
info_panel_color = video.BLUE
robot_background = video.GREEN

num_compost = 0
num_garbage = 0
num_paper = 0
num_recycling = 0

stop = False

def signal_handler(signal, frame) :
   global stop
   stop = True

''' create a rectangle using the top left corner, bottom right corner, and colour given
return: void
'''
def gen_rec (x1, y1, x2, y2, colour):
   video.box(x1, y1, x2, y2, colour)

''' create a square using the top left corner, side length, and colour given
return: void
'''
def gen_square (x1, y1, d, colour):
   video.box(x1, y1, x1 + d, y1 + d, colour)

''' draw the bar graph using current garbage values
return: void
'''
def draw_bar_graph ():
   middle_y = (screen_y / 2) - 1
   boarder_w = 2
   screen_xb = screen_x-1 
   info_corner = 7 + boarder_w
   garbot_corner = screen_xb - 60
   info_bcorner = garbot_corner - 10
   legend_corner_x = info_corner + 5
   legend_corner_y = info_corner + 31
   barcorner_x = legend_corner_x + 63

   gen_rec (legend_corner_x + 55, legend_corner_y - 9, info_bcorner - 8, middle_y-(boarder_w/2)-6, video.WHITE)

   garbage_sum = num_compost + num_garbage + num_paper + num_recycling
   if garbage_sum == 0:
      garbage_sum = 1

   comp = math.ceil(73.0 * (num_compost / float(garbage_sum)))
   garb = math.ceil(73.0 * (num_garbage / float(garbage_sum)))
   paper = math.ceil(73.0 * (num_paper / float(garbage_sum)))
   recyc = math.ceil(73.0 * (num_recycling / float(garbage_sum)))

   gen_rec (barcorner_x, legend_corner_y + 66 - comp, barcorner_x + 33, middle_y-(boarder_w/2)-6, video.GREEN)
   gen_rec (barcorner_x + 41, legend_corner_y + 66 - garb, barcorner_x + 74, middle_y-(boarder_w/2)-6, boarder_color)
   gen_rec (barcorner_x + 82, legend_corner_y + 66 - paper, barcorner_x + 115, middle_y-(boarder_w/2)-6, video.YELLOW)
   gen_rec (barcorner_x + 123, legend_corner_y + 66 - recyc, barcorner_x + 156, middle_y-(boarder_w/2)-6, dark_blue)

''' create initial gui scene
return: void
'''
def gen_init_gui ():
   middle_y = (screen_y / 2) - 1
   boarder_w = 2
   screen_xb = screen_x-1 
   screen_yb = screen_y-1
   info_corner = 7 + boarder_w
   garbot_corner = screen_xb - 60
   info_bcorner = garbot_corner - 10
   legend_corner_x = info_corner + 5
   legend_corner_y = info_corner + 31

   gen_rec (boarder_w, boarder_w, screen_xb, screen_yb, background_color) # Background

   gen_rec (info_corner - boarder_w, info_corner - boarder_w, info_bcorner + boarder_w, screen_yb-8 + boarder_w, boarder_color) # Info panel boarder
   gen_rec (info_corner, info_corner, info_bcorner, middle_y-(boarder_w/2) + 5, info_panel_color) # Info panel 
   gen_rec (info_corner, middle_y+(boarder_w/2) + 7, info_bcorner, screen_yb-8, info_panel_color) 

   gen_rec (legend_corner_x, middle_y+(boarder_w/2) + 12, legend_corner_x + 129, screen_yb-13, boarder_color)
   gen_rec (legend_corner_x + boarder_w, middle_y+(boarder_w/2) + 12 + boarder_w, legend_corner_x + 129 - boarder_w, screen_yb-13 - boarder_w, 0x630A)
   gen_rec (legend_corner_x + 142, middle_y+(boarder_w/2) + 44, legend_corner_x + 233, middle_y+(boarder_w/2) + 59, boarder_color)

   # Garbage stat display
   gen_square (legend_corner_x, legend_corner_y, 10, video.GREEN) 
   gen_square (legend_corner_x, legend_corner_y + 16, 10, boarder_color) 
   gen_square (legend_corner_x, legend_corner_y + 32, 10, video.YELLOW) 
   gen_square (legend_corner_x, legend_corner_y + 48, 10, dark_blue) 
   gen_rec (legend_corner_x + 53, legend_corner_y - 11, info_bcorner - 6, middle_y-(boarder_w/2)-4, boarder_color) 
   draw_bar_graph()

   # Garbot panel
   gen_rec (garbot_corner - boarder_w, info_corner - boarder_w, screen_xb-5 + boarder_w, screen_yb-10 + boarder_w, boarder_color) # Garbot panel boarder 
   gen_rec (garbot_corner, info_corner, screen_xb-5, screen_yb-10, robot_background) # Garbot panel
   draw_green_garbot(screen_xb - 64, screen_yb - 225)
   draw_black_garbot(screen_xb - 61, screen_yb - 172)
   draw_yellow_garbot(screen_xb - 62, screen_yb - 118)
   draw_blue_garbot(screen_xb - 62, screen_yb - 67)

