def set_bar_text ():
   # Bar numbers
   msg = "%03d" % (num_compost)
   video.text(char_x - 58, char_y - 33, msg)
   msg = "%03d" % (num_garbage)
   video.text(char_x - 48, char_y - 33, msg)
